import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid'; // Import uuid

// Define the type for Google Search Results
interface GoogleSearchResult {
  title: string;
  snippet: string;
  link: string;
}

// Ensure Tailwind CSS is available in the environment.
// This code assumes Tailwind CSS is configured and available.

// Define your FastAPI backend URL
const API_BASE_URL = "http://127.0.0.1:8000/api/v1/home_buying"; // Corrected to match your router prefix

function App() {
  const [userId, setUserId] = useState<string | null>(null);
  const [isAppReady, setIsAppReady] = useState(false);
  const [preferences, setPreferences] = useState('');
  const [mockProperties, setMockProperties] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedProperty, setSelectedProperty] = useState<any | null>(null);
  const [message, setMessage] = useState<{ text: string; type: string }>({ text: '', type: '' });

  const [negotiationResult, setNegotiationResult] = useState('');
  const [showNegotiationModal, setShowNegotiationModal] = useState(false);
  const [legalGuidance, setLegalGuidance] = useState('');
  const [showLegalModal, setShowLegalModal] = useState(false);
  const [mortgageRecommendations, setMortgageRecommendations] = useState<any[]>([]);
  const [showMortgageModal, setShowMortgageModal] = useState(false);
  const [targetNegotiationPrice, setTargetNegotiationPrice] = useState('');

  const [googleSearchResults, setGoogleSearchResults] = useState<GoogleSearchResult[]>([]);

  const [neighborhoodInsights, setNeighborhoodInsights] = useState('');
  const [showNeighborhoodModal, setShowNeighborhoodModal] = useState(false);
  const [marketingDescription, setMarketingDescription] = useState('');
  const [showMarketingDescriptionModal, setShowMarketingDescriptionModal] = useState(false);

  // Initialize unique user ID from localStorage or generate a new one
  useEffect(() => {
    const currentUserId: string = localStorage.getItem('ai_home_buyer_user_id') || uuidv4();
    localStorage.setItem('ai_home_buyer_user_id', currentUserId);

    setUserId(currentUserId);
    console.log('User ID set:', currentUserId);
    setIsAppReady(true); // App is ready once user ID is determined
  }, []);

  // Load user preferences from backend when app is ready and userId is available
  useEffect(() => {
    const loadUserPreferencesFromBackend = async () => {
      if (isAppReady && userId) {
        try {
          // Assuming a /load_preferences endpoint exists in your backend
          const response = await fetch(`${API_BASE_URL}/load_preferences/${userId}`);
          const result = await response.json();

          if (response.ok && result.status === 'success' && result.data) {
            setPreferences(result.data.homePreferences || '');
            setMockProperties(result.data.lastSearchResults || []);
            console.log("User preferences loaded from backend:", result.data);
          } else {
            console.log("No user preferences found on backend or error:", result.detail || 'Unknown error');
            setPreferences('');
            setMockProperties([]);
          }
        } catch (error: any) {
          console.error("Error fetching user preferences from backend:", error);
          setMessage({ text: `Error loading preferences: ${error.message}`, type: 'error' });
        }
      }
    };

    loadUserPreferencesFromBackend();
  }, [isAppReady, userId]); // Re-run when app readiness or userId changes

  // Function to save user preferences and results to backend
  const savePreferences = async (currentPreferences: string, currentResults: any[]) => {
    if (!userId) {
      setMessage({ text: 'User not ready. Cannot save preferences.', type: 'error' });
      return;
    }
    try {
      // Assuming a /save_preferences endpoint exists in your backend
      const response = await fetch(`${API_BASE_URL}/save_preferences`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ user_id: userId, preferences: { homePreferences: currentPreferences, lastSearchResults: currentResults } })
      });

      if (response.ok) {
        setMessage({ text: 'Preferences saved successfully!', type: 'success' });
      } else {
        const errorResult = await response.json();
        setMessage({ text: `Failed to save preferences: ${errorResult.detail || 'Unknown error'}`, type: 'error' });
      }

    } catch (error: any) {
      console.error("Error saving preferences to backend:", error);
      setMessage({ text: `Error saving preferences: ${error.message}`, type: 'error' });
    }
  };

  const handleSearch = async () => {
    if (!preferences.trim()) {
      setMessage({ text: 'Please describe your desired home.', type: 'info' });
      return;
    }
    if (!userId) {
      setMessage({ text: 'User ID not ready. Please try again.', type: 'error' });
      return;
    }

    setIsLoading(true);
    setMockProperties([]);
    setGoogleSearchResults([]);
    setMessage({ text: '', type: '' }); // Clear previous messages

    try {
      // This endpoint would typically route to your main AI agent logic (e.g., process_query)
      const response = await fetch(`${API_BASE_URL}/process_query`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ user_id: userId, query: preferences })
      });

      const result = await response.json();

      if (response.ok && result.status === 'success' && result.data) {
        if (result.data.type === 'properties') {
          setMockProperties(result.data.data);
          await savePreferences(preferences, result.data.data);
          setMessage({ text: 'Simulated search results generated successfully!', type: 'success' });
        } else if (result.data.type === 'google_search') {
          setGoogleSearchResults(result.data.data);
          setMessage({ text: 'No simulated properties found. Displaying Google Search results.', type: 'info' });
        } else {
          setMessage({ text: 'Unexpected response type from backend.', type: 'error' });
        }
      } else {
        setMessage({ text: `Backend error: ${result.detail || 'Unknown error'}`, type: 'error' });
      }
    } catch (error: any) {
      console.error("Error calling backend search API:", error);
      setMessage({ text: `Error during search: ${error.message}. Please ensure backend is running.`, type: 'error' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleNegotiationSimulation = async (property: any) => {
    if (!targetNegotiationPrice.trim()) {
      setMessage({ text: 'Please enter a target negotiation price.', type: 'info' });
      return;
    }
    if (!userId) {
      setMessage({ text: 'User ID not ready. Please try again.', type: 'error' });
      return;
    }
    setIsLoading(true);
    setMessage({ text: 'Simulating negotiation...', type: 'info' });
    try {
      const response = await fetch(`${API_BASE_URL}/process_query`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: userId,
          query: `Simulate negotiation for ${property.name}`,
          property_details: property,
          target_negotiation_price: targetNegotiationPrice
        })
      });
      const result = await response.json();
      if (response.ok && result.status === 'success' && result.data && result.data.type === 'negotiation_result') {
        setNegotiationResult(result.data.data);
        setShowNegotiationModal(true);
        setMessage({ text: 'Negotiation simulation complete!', type: 'success' });
      } else {
        setMessage({ text: `Failed to simulate negotiation: ${result.detail || 'Unknown error'}`, type: 'error' });
      }
    } catch (error: any) {
      console.error("Error calling negotiation API:", error);
      setMessage({ text: `Error simulating negotiation: ${error.message}`, type: 'error' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleLegalGuidance = async () => {
    if (!userId) {
      setMessage({ text: 'User ID not ready. Please try again.', type: 'error' });
      return;
    }
    setIsLoading(true);
    setMessage({ text: 'Fetching legal guidance...', type: 'info' });
    try {
      const response = await fetch(`${API_BASE_URL}/process_query`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ user_id: userId, query: "Provide legal and paperwork guidance" })
      });
      const result = await response.json();
      if (response.ok && result.status === 'success' && result.data && result.data.type === 'legal_guidance') {
        setLegalGuidance(result.data.data);
        setShowLegalModal(true);
        setMessage({ text: 'Legal guidance fetched!', type: 'success' });
      } else {
        setMessage({ text: `Failed to get legal guidance: ${result.detail || 'Unknown error'}`, type: 'error' });
      }
    } catch (error: any) {
      console.error("Error calling legal guidance API:", error);
      setMessage({ text: `Error fetching legal guidance: ${error.message}`, type: 'error' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleMortgageRecommendation = async (propertyPrice: string) => {
    if (!userId) {
      setMessage({ text: 'User ID not ready. Please try again.', type: 'error' });
      return;
    }
    setIsLoading(true);
    setMessage({ text: 'Fetching mortgage recommendations...', type: 'info' });
    try {
      const response = await fetch(`${API_BASE_URL}/process_query`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: userId,
          query: `Get mortgage recommendations for a property priced at ${propertyPrice}`,
          property_details: { price: propertyPrice }
        })
      });
      const result = await response.json();
      if (response.ok && result.status === 'success' && result.data && result.data.type === 'mortgage_recommendations') {
        if (Array.isArray(result.data.data)) {
          setMortgageRecommendations(result.data.data);
        } else {
          setMortgageRecommendations([{
            bankName: "General Advice",
            interestRate: "Varies",
            loanTenure: "Varies",
            highlight: result.data.data
          }]);
        }
        setShowMortgageModal(true);
        setMessage({ text: 'Mortgage recommendations loaded!', type: 'success' });
      } else {
        setMessage({ text: `Failed to get mortgage recommendations: ${result.detail || 'Unknown error'}`, type: 'error' });
      }
    } catch (error: any) {
      console.error("Error calling mortgage API:", error);
      setMessage({ text: `Error fetching mortgage recommendations: ${error.message}`, type: 'error' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleNeighborhoodInsights = async (location: string) => {
    if (!userId) {
      setMessage({ text: 'User ID not ready. Please try again.', type: 'error' });
      return;
    }
    setIsLoading(true);
    setMessage({ text: 'Generating neighborhood insights...', type: 'info' });
    try {
      const response = await fetch(`${API_BASE_URL}/process_query`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: userId,
          query: `Provide neighborhood insights for ${location}`,
          property_details: { location: location }
        })
      });
      const result = await response.json();
      if (response.ok && result.status === 'success' && result.data && result.data.type === 'neighborhood_insights') {
        setNeighborhoodInsights(result.data.data);
        setShowNeighborhoodModal(true);
        setMessage({ text: 'Neighborhood insights generated!', type: 'success' });
      } else {
        setMessage({ text: `Failed to get neighborhood insights: ${result.detail || 'Unknown error'}`, type: 'error' });
      }
    } catch (error: any) {
      console.error("Error calling neighborhood insights API:", error);
      setMessage({ text: `Error generating neighborhood insights: ${error.message}`, type: 'error' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleGenerateMarketingDescription = async (property: any) => {
    if (!userId) {
      setMessage({ text: 'User ID not ready. Please try again.', type: 'error' });
      return;
    }
    setIsLoading(true);
    setMessage({ text: 'Generating marketing description...', type: 'info' });
    try {
      const response = await fetch(`${API_BASE_URL}/process_query`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: userId,
          query: `Generate marketing description for ${property.name}`,
          property_details: property
        })
      });
      const result = await response.json();
      if (response.ok && result.status === 'success' && result.data && result.data.type === 'marketing_description') {
        setMarketingDescription(result.data.data);
        setShowMarketingDescriptionModal(true);
        setMessage({ text: 'Marketing description generated!', type: 'success' });
      } else {
        setMessage({ text: `Failed to generate marketing description: ${result.detail || 'Unknown error'}`, type: 'error' });
      }
    } catch (error: any) {
      console.error("Error calling marketing description API:", error);
      setMessage({ text: `Error generating marketing description: ${error.message}`, type: 'error' });
    } finally {
      setIsLoading(false);
    }
  };

  // UPDATED handleBookVisit function
  const handleBookVisit = async (property: any) => {
    if (!userId) {
      setMessage({ text: 'User ID not ready. Please try again.', type: 'error' });
      return;
    }
    
    // Clear previous message and set a loading/info message
    setMessage({ text: `Attempting to book a visit for ${property.name}...`, type: 'info' });

    try {
      // Direct call to the simulated backend endpoint
      const response = await fetch(`${API_BASE_URL}/book_visit`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(property) // Send property details to the backend
      });

      const result = await response.json(); // Parse the JSON response from FastAPI

      if (response.ok && result.status === 'success') {
        setMessage({ text: result.message, type: 'success' }); // Display success message from backend
      } else {
        // Handle backend errors (e.g., if FastAPI returns status: 'error')
        setMessage({ text: `Failed to book visit: ${result.message || result.detail || 'Unknown error'}`, type: 'error' });
      }
    } catch (error: any) {
      console.error("Error calling book visit API:", error);
      setMessage({ text: `Error simulating book visit: ${error.message}. Please ensure backend is running.`, type: 'error' });
    }
  };

  const handlePaperwork = async (property: any) => {
    if (!userId) {
      setMessage({ text: 'User ID not ready. Please try again.', type: 'error' });
      return;
    }
    setMessage({ text: `Simulating paperwork for ${property.name}. (No actual documents generated)`, type: 'info' });
    try {
      // Direct call to the simulated backend endpoint
      // Assuming a /handle_paperwork endpoint exists in your backend
      const response = await fetch(`${API_BASE_URL}/handle_paperwork`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(property)
      });
      const result = await response.json();
      if (response.ok && result.status === 'success') {
        setMessage({ text: result.message, type: 'success' });
      } else {
        setMessage({ text: `Failed to handle paperwork: ${result.message || result.detail || 'Unknown error'}`, type: 'error' });
      }
    } catch (error: any) {
      console.error("Error simulating paperwork:", error);
      setMessage({ text: `Error simulating paperwork: ${error.message}`, type: 'error' });
    }
  };

  const handleCloseModal = () => {
    setSelectedProperty(null);
    setShowNegotiationModal(false);
    setShowLegalModal(false);
    setShowMortgageModal(false);
    setShowNeighborhoodModal(false);
    setShowMarketingDescriptionModal(false);
    setNegotiationResult('');
    setLegalGuidance('');
    setMortgageRecommendations([]);
    setTargetNegotiationPrice('');
    setNeighborhoodInsights('');
    setMarketingDescription('');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4 sm:p-6 font-inter text-gray-800 flex flex-col items-center">
      <div className="w-full max-w-4xl bg-white rounded-xl shadow-2xl p-6 sm:p-8 space-y-6">
        <h1 className="text-3xl sm:text-4xl font-extrabold text-center text-indigo-700 mb-4">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 sm:h-10 sm:w-10 inline-block mr-2 text-blue-500" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
            <path strokeLinecap="round" strokeLinejoin="round" d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m0 0l7 7m-9-9v10a1 1 0 001 1h3m-6-11h8a2 2 0 012 2v8a2 2 0 01-2 2h-8a2 2 0 01-2-2v-8a2 2 0 012-2z" />
          </svg>
          AI Home Buying Assistant
        </h1>

        {userId && (
          <p className="text-sm text-center text-gray-600 mb-4">
            Your User ID: <span className="font-semibold text-indigo-600 break-all">{userId}</span>
          </p>
        )}

        {message.text && (
          <div className={`p-3 rounded-lg text-center font-medium ${
            message.type === 'success' ? 'bg-green-100 text-green-700' :
            message.type === 'error' ? 'bg-red-100 text-red-700' :
            'bg-blue-100 text-blue-700'
          }`}>
            {message.text}
          </div>
        )}

        <div className="space-y-4">
          <label htmlFor="preferences" className="block text-lg font-semibold text-gray-700">
            Tell me about your dream home:
          </label>
          <textarea
            id="preferences"
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-transparent transition duration-200 resize-y min-h-[100px]"
            placeholder="e.g., 'A 3-bedroom apartment in Koramangala, Bangalore, with a budget of 1.2 Crore, near good schools and a park. Modern design, good natural light, and a balcony.' Or 'Find me a 2BHK in a nearby area with high rental under Rs 50 Lakhs.'"
            value={preferences}
            onChange={(e) => setPreferences(e.target.value)}
            disabled={isLoading || !isAppReady}
          ></textarea>
          <button
            onClick={handleSearch}
            className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:-translate-y-0.5 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
            disabled={isLoading || !isAppReady}
          >
            {isLoading ? (
              <>
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Searching...
              </>
            ) : (
              <>
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
                Find My Home
              </>
            )}
          </button>
        </div>

        {mockProperties.length > 0 && (
          <div className="mt-8">
            <h2 className="text-2xl font-bold text-indigo-700 mb-4 text-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-7 w-7 inline-block mr-2 text-blue-500" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                <path strokeLinecap="round" strokeLinejoin="round" d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m-1 4h1m8-10h1m-1 4h1m-1 4h1m-8 4v2m8-2v2" />
              </svg>
              Suggested Properties (Simulated)
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {mockProperties.map((property) => (
                <div key={property.id} className="bg-white border border-gray-200 rounded-xl shadow-lg overflow-hidden transform transition duration-300 hover:scale-105 hover:shadow-xl">
                  <img
                    src={property.imageUrl}
                    alt={property.name}
                    className="w-full h-48 object-cover"
                    onError={(e: React.SyntheticEvent<HTMLImageElement, Event>) => {
                      const target = e.target as HTMLImageElement;
                      target.onerror = null;
                      target.src = `https://placehold.co/400x300/E0F2F7/000000?text=Home`;
                    }}
                  />
                  <div className="p-4 space-y-2">
                    <h3 className="text-xl font-bold text-indigo-800">{property.name}</h3>
                    <p className="text-gray-600 text-sm flex items-center">
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1 text-blue-400" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                        <path strokeLinecap="round" strokeLinejoin="round" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                      {property.location}
                    </p>
                    <p className="text-2xl font-extrabold text-green-600">{property.price}</p>
                    <div className="flex items-center text-gray-700 text-sm space-x-4">
                      <span className="flex items-center">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1 text-purple-400" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m0 0l7 7m-9-9v10a1 1 0 001 1h3m-6-11h8a2 2 0 012 2v8a2 2 0 01-2 2h-8a2 2 0 01-2-2v-8a2 2 0 012-2z" />
                        </svg>
                        {property.bedrooms} Bed
                      </span>
                      <span className="flex items-center">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1 text-yellow-400" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.592 1L21 12h-3.812c-.668 1.053-1.644 1.83-2.812 2.227M12 8V4m0 8v4m-6 0H4M8 8H4m7-4h4m-4 8h4m-4 8h4" />
                        </svg>
                        {property.bathrooms} Bath
                      </span>
                      <span className="flex items-center">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1 text-orange-400" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M13 10V3L4 14h7v7l9-11h-7z" />
                        </svg>
                        {property.areaSqFt} SqFt
                      </span>
                    </div>
                    <p className="text-gray-700 text-sm mt-2 line-clamp-3">{property.description}</p>
                    <div className="flex justify-between mt-4 space-x-2">
                      <button
                        onClick={() => setSelectedProperty(property)}
                        className="flex-1 bg-blue-500 hover:bg-blue-600 text-white text-sm font-semibold py-2 px-3 rounded-lg transition duration-200 shadow-sm hover:shadow-md"
                      >
                        View Details
                      </button>
                      <button
                        onClick={() => handleBookVisit(property)}
                        className="flex-1 bg-green-500 hover:bg-green-600 text-white text-sm font-semibold py-2 px-3 rounded-lg transition duration-200 shadow-sm hover:shadow-md"
                      >
                        Book Visit
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {googleSearchResults.length > 0 && (
          <div className="mt-8 border-t pt-6 border-gray-200">
            <h2 className="text-2xl font-bold text-gray-700 mb-4 text-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-7 w-7 inline-block mr-2 text-blue-500" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                <path strokeLinecap="round" strokeLinejoin="round" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
              </svg>
              Google Search Results
            </h2>
            <div className="space-y-4">
              {googleSearchResults.map((result, index) => (
                <div key={index} className="bg-gray-50 p-4 rounded-lg shadow-sm border border-gray-200">
                  <a href={result.link} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline text-lg font-semibold">
                    {result.title}
                  </a>
                  <p className="text-gray-700 text-sm mt-1">{result.snippet}</p>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Global Action Buttons */}
        <div className="mt-8 pt-6 border-t border-gray-200 flex flex-wrap justify-center gap-4">
          <button
            onClick={handleLegalGuidance}
            className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-3 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:-translate-y-0.5 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
            disabled={isLoading}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
              <path strokeLinecap="round" strokeLinejoin="round" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
            </svg>
            Legal & Paperwork Guide
          </button>
        </div>
      </div>

      {/* Property Details Modal */}
      {selectedProperty && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl shadow-2xl p-6 sm:p-8 max-w-2xl w-full relative">
            <button
              onClick={handleCloseModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-2xl font-bold"
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold text-indigo-700 mb-4">{selectedProperty.name}</h2>
            <img
              src={selectedProperty.imageUrl}
              alt={selectedProperty.name}
              className="w-full h-64 object-cover rounded-lg mb-4"
              onError={(e: React.SyntheticEvent<HTMLImageElement, Event>) => {
                const target = e.target as HTMLImageElement;
                target.onerror = null;
                target.src = `https://placehold.co/600x400/E0F2F7/000000?text=Home`;
              }}
            />
            <p className="text-gray-700 text-lg mb-2 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-blue-500" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                <path strokeLinecap="round" strokeLinejoin="round" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                <path strokeLinecap="round" strokeLinejoin="round" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
              <span className="font-semibold">Location:</span> {selectedProperty.location}
            </p>
            <p className="text-green-600 text-3xl font-extrabold mb-4">{selectedProperty.price}</p>
            <div className="grid grid-cols-2 gap-4 mb-4 text-gray-700">
              <p className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-purple-500" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m0 0l7 7m-9-9v10a1 1 0 001 1h3m-6-11h8a2 2 0 012 2v8a2 2 0 01-2 2h-8a2 2 0 01-2-2v-8a2 2 0 012-2z" />
                </svg>
                <span className="font-semibold">Bedrooms:</span> {selectedProperty.bedrooms}
              </p>
              <p className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-yellow-500" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.592 1L21 12h-3.812c-.668 1.053-1.644 1.83-2.812 2.227M12 8V4m0 8v4m-6 0H4M8 8H4m7-4h4m-4 8h4m-4 8h4" />
                </svg>
                <span className="font-semibold">Bathrooms:</span> {selectedProperty.bathrooms}
              </p>
              <p className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-orange-500" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M13 10V3L4 14h7v7l9-11h-7z" />
                </svg>
                <span className="font-semibold">Area:</span> {selectedProperty.areaSqFt} SqFt
              </p>
            </div>
            <p className="text-gray-800 mb-4">{selectedProperty.description}</p>
            <div className="flex flex-col sm:flex-row justify-end space-y-3 sm:space-y-0 sm:space-x-4">
              <input
                type="text"
                placeholder="Target price for negotiation"
                value={targetNegotiationPrice}
                onChange={(e) => setTargetNegotiationPrice(e.target.value)}
                className="p-2 border border-gray-300 rounded-lg w-full sm:w-auto"
              />
              <button
                onClick={() => handleNegotiationSimulation(selectedProperty)}
                className="bg-yellow-600 hover:bg-yellow-700 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                disabled={isLoading}
              >
                Negotiation Assistant 
              </button>
              <button
                onClick={() => handleMortgageRecommendation(selectedProperty.price)}
                className="bg-teal-600 hover:bg-teal-700 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                disabled={isLoading}
              >
                Mortgage Recommendation 
              </button>
              <button
                onClick={() => handleNeighborhoodInsights(selectedProperty.location)}
                className="bg-cyan-600 hover:bg-cyan-700 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                disabled={isLoading}
              >
                Neighborhood Insights 
              </button>
              <button
                onClick={() => handleGenerateMarketingDescription(selectedProperty)}
                className="bg-pink-600 hover:bg-pink-700 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                disabled={isLoading}
              >
                Generate Marketing Description 
              </button>
              <button
                onClick={() => handleBookVisit(selectedProperty)}
                className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300"
              >
                Book Visit
              </button>
              <button
                onClick={() => handlePaperwork(selectedProperty)}
                className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300"
              >
                Handle Paperwork
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Negotiation Modal */}
      {showNegotiationModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl shadow-2xl p-6 sm:p-8 max-w-xl w-full relative">
            <button
              onClick={handleCloseModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-2xl font-bold"
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold text-yellow-700 mb-4 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                <path strokeLinecap="round" strokeLinejoin="round" d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z" />
              </svg>
              Negotiation Simulation
            </h2>
            <p className="text-gray-800 mb-4">{negotiationResult}</p>
            <div className="flex justify-end">
              <button
                onClick={handleCloseModal}
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Legal Guidance Modal */}
      {showLegalModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl shadow-2xl p-6 sm:p-8 max-w-2xl w-full relative">
            <button
              onClick={handleCloseModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-2xl font-bold"
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold text-purple-700 mb-4 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                <path strokeLinecap="round" strokeLinejoin="round" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
              </svg>
              Legal & Paperwork Guide
            </h2>
            <div className="prose max-w-none text-gray-800 mb-4" dangerouslySetInnerHTML={{ __html: legalGuidance.replace(/\n/g, '<br/>') }}></div>
            <div className="flex justify-end">
              <button
                onClick={handleCloseModal}
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Mortgage Recommendation Modal */}
      {showMortgageModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl shadow-2xl p-6 sm:p-8 max-w-xl w-full relative">
            <button
              onClick={handleCloseModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-2xl font-bold"
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold text-teal-700 mb-4 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                <path strokeLinecap="round" strokeLinejoin="round" d="M9 8h6m-5 0h.01M9 12h6m-5 0h.01M9 16h6m-5 0h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              Mortgage Recommendations
            </h2>
            {mortgageRecommendations.length > 0 ? (
              <ul className="space-y-4 mb-4">
                {mortgageRecommendations.map((rec, index) => (
                  <li key={index} className="bg-gray-50 p-4 rounded-lg border border-gray-200">
                    <p className="font-semibold text-lg text-gray-900">{rec.bankName}</p>
                    <p className="text-gray-700">Interest Rate: <span className="font-medium text-green-600">{rec.interestRate}</span></p>
                    <p className="text-gray-700">Loan Tenure: <span className="font-medium">{rec.loanTenure}</span></p>
                    <p className="text-gray-700">Highlight: <span className="italic">{rec.highlight}</span></p>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-600">No mortgage recommendations available.</p>
            )}
            <div className="flex justify-end">
              <button
                onClick={handleCloseModal}
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Neighborhood Insights Modal */}
      {showNeighborhoodModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl shadow-2xl p-6 sm:p-8 max-w-2xl w-full relative">
            <button
              onClick={handleCloseModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-2xl font-bold"
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold text-cyan-700 mb-4 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                <path strokeLinecap="round" strokeLinejoin="round" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                <path strokeLinecap="round" strokeLinejoin="round" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
              Neighborhood Insights
            </h2>
            <div className="prose max-w-none text-gray-800 mb-4" dangerouslySetInnerHTML={{ __html: neighborhoodInsights.replace(/\n/g, '<br/>') }}></div>
            <div className="flex justify-end">
              <button
                onClick={handleCloseModal}
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Marketing Description Modal */}
      {showMarketingDescriptionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl shadow-2xl p-6 sm:p-8 max-w-2xl w-full relative">
            <button
              onClick={handleCloseModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-2xl font-bold"
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold text-pink-700 mb-4 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                <path strokeLinecap="round" strokeLinejoin="round" d="M7 8h10M7 12h10M7 16h10M4 16v-4a2 2 0 012-2h2m0 0a2 2 0 012-2h2m0 0a2 2 0 012-2h2m0 0a2 2 0 012-2v-4m-4 4h.01M17 16h.01" />
              </svg>
              Marketing Description
            </h2>
            <div className="prose max-w-none text-gray-800 mb-4" dangerouslySetInnerHTML={{ __html: marketingDescription.replace(/\n/g, '<br/>') }}></div>
            <div className="flex justify-end">
              <button
                onClick={handleCloseModal}
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
